"""
5. Chikou Span – Чикоу Спан. Представляет собой цену закрытия, сдвинутую на m периодов назад, при этом на дневном графике автор рекомендует применять m=26.
![image.png](data:image/png;base64,)
Приведенные выше настройки индикатора Ишимоку являются базовыми и могут меняться в зависимости от задач и целей, стоящих перед инвестором.
"""

def chikou_span(data, m, tiker):
    data_chikou = data['Close'][tiker].dropna()
    new_data = data_chikou.copy()

    # data_chikou.index = data_chikou.index.date - datetime.timedelta(days=m)
    #изменить индекс на 26 назад
    for i in range(len(data_chikou)-m):
        new_data[i] = data_chikou[i+m]
    new_data = new_data[:-m]
    return new_data

def show_plot(tenkan_sen_value, kijun_sen_value, senkou_spanA_value, senkou_spanB_value, chikou_span_value):
    plt.figure(figsize=(10, 5))
    plt.plot(tenkan_sen_value)
    plt.plot(kijun_sen_value)
    plt.plot(senkou_spanA_value)  # зеленая
    plt.plot(senkou_spanB_value)  # красная
    plt.plot(chikou_span_value)  # фиолетовая

    plt.show()

#Анализ текущего дня согласно Ишимоку
def trend(data_ichimoko, tenkan_sen_value, kijun_sen_value, senkou_spanA_value, senkou_spanB_value, chikou_span_value, tiker):
    cost = data_ichimoko['Close'][tiker].dropna()

    #цена > (стандарта и разворота) + выше облака, стандарт >= разворотной, стандарт выше облака, опаздывающая > цены
    if (cost[-1] > (tenkan_sen_value[0] and kijun_sen_value[0])) and \
            cost[-1] > (senkou_spanA_value[25] and senkou_spanB_value[25]) and \
            (tenkan_sen_value[0] >= kijun_sen_value[0]) and \
            (tenkan_sen_value[0] > (senkou_spanA_value[25] and senkou_spanB_value[25])) \
            and (chikou_span_value[-1] > cost[-26]):
        print(f'{tiker} - Up Trend ')
        return 1
    if (tenkan_sen_value[0] <= kijun_sen_value[0]) and \
            tenkan_sen_value[0] < (senkou_spanA_value[25] and senkou_spanB_value[25]) and \
            (chikou_span_value[-1] < cost[-26])\
            and (cost[-1] < (tenkan_sen_value[0] and kijun_sen_value[0])) and \
            cost[-1] < (senkou_spanA_value[25] and senkou_spanB_value[25]):
        print(f'{tiker} - Down Trend ')
    # if cost >
        return 1
    return 0

if __name__ == '__main__':
    start = datetime.datetime.now()
    path = Path('Search.txt')
    searchList = open_file_and_split(path)

    start_date = '2021-06-01'
    data = yf.download(searchList, start=start_date)
    data_ichimoko = data.dropna(how='all')

    # tiker = 'BABA'
    count = 0
    for tiker in searchList:
        tenkan_sen_value = tenkan_sen(data_ichimoko, 9, tiker)
        kijun_sen_value = tenkan_sen(data_ichimoko, 26, tiker)
        senkou_spanA_value = senkou_spanA(tenkan_sen_value, kijun_sen_value, 26)
        senkou_spanB_value = senkou_spanB(tenkan_sen(data_ichimoko, 52, tiker), tenkan_sen_value, 26)
        chikou_span_value = chikou_span(data_ichimoko, 26, tiker)

        show_plot(tenkan_sen_value, kijun_sen_value, senkou_spanA_value, senkou_spanB_value, chikou_span_value)

        count += trend(data_ichimoko, tenkan_sen_value, kijun_sen_value, senkou_spanA_value, senkou_spanB_value, chikou_span_value, tiker)
    print(f'\n Up Trend - (цена > (с и р) + выше облака, с >= р, с Выше облака, lag > цены)'
          f'\n Down Trend - (цена < (с и р) + ниже облака, с <= р, c Ниже облака, lag < цены)'
          f'\n количесво опрделенных трендов = {count} из {len(searchList)}')
    # show_plot(tenkan_sen_value, kijun_sen_value, senkou_spanA_value, senkou_spanB_value, chikou_span_value)
    end = datetime.datetime.now() - start
    print(f'\n  Продолжительность: {end}' )
